import{pascalToKebab}from'./util.js';const registerControllers=(...controllers)=>{const allUndefinedElements=[...document.querySelectorAll(":not(:defined)")];allUndefinedElements.forEach(el=>el.setAttribute("data-controller",el.localName));for(let controller of controllers){let config={};if(Array.isArray(controller)){[controller,config={}]=controller}const controllerName=controller.name;const controllerTag=config&&config.name?config.name:pascalToKebab(controllerName.replace("Controller",""));if(!controllerTag.includes("-")){console.error(`[${controllerName}] Controller tag name must contain a hyphen but got <${controllerTag}>`)}let opts={};if(controller.__extendTag__)opts.extends=controller.__extendTag__;window.customElements.define(controllerTag,controller,opts)}};export{registerControllers}
